mapper.update:
UPDATE medicine_warehouse_table
SET medicine_warehouse_table.warehouse_count = #{warehouse_count},
    medicine_warehouse_table.canuse_count = #{canuse_count}
WHERE
    medicine_warehouse_table.medicine_code = #{medicine_code}
  AND medicine_warehouse_table.medicine_batch_number = #{medicine_batch_number}
  AND medicine_warehouse_table.create_time=STR_TO_DATE(#{create_time}, '%Y%m%d');

UPDATE medicine_warehouse_table
SET medicine_warehouse_table.canuse_count = #{canuse_count}
WHERE
    medicine_warehouse_table.medicine_code = #{medicine_code}
  AND medicine_warehouse_table.medicine_batch_number = #{medicine_batch_number}
  AND medicine_warehouse_table.create_time=STR_TO_DATE(#{create_time}, '%Y%m%d');
